mapper.update:
UPDATE 
	attendance 
SET 
	update_start_time = CONCAT(DATE(update_start_time), ' ', #{updateStartTime}, ':00'), 
	update_end_time = CONCAT(DATE(update_end_time), ' ', #{updateEndTime}, ':00'),
	update_reason = #{updateReason}
WHERE 
	emp_no = #{empNo} 
	AND date = #{date}

mapper.select:
SELECT emp_no empNo, date, DATE_FORMAT(start_time, '%H:%i') startTime, DATE_FORMAT(end_time, '%H:%i') endTime, DATE_FORMAT(update_start_time, '%H:%i') updateStartTime, DATE_FORMAT(update_end_time, '%H:%i') updateEndTime, update_reason updateReason, CASE WHEN approval_state = 1 THEN '미승인' WHEN approval_state = 2 THEN '승인' WHEN approval_state = 9 THEN '반려' ELSE '알 수 없음' END approvalState, CASE WHEN start_time = update_start_time AND end_time = update_end_time THEN 'x' ELSE 'o' END updateStatus FROM attendance WHERE emp_no = #{empNo}
AND date BETWEEN #{startDate} AND #{endDate}
ORDER BY date DESC
LIMIT #{beginRow}, #{rowPerPage}

SELECT a.emp_no empNo, e.emp_name empName, a.date date, DATE_FORMAT(a.start_time, '%H:%i') startTime, DATE_FORMAT(a.end_time, '%H:%i') endTime, DATE_FORMAT(a.update_start_time, '%H:%i') updateStartTime, DATE_FORMAT(a.update_end_time, '%H:%i') updateEndTime, CASE WHEN a.approval_state = 1 THEN '미승인' WHEN a.approval_state = 2 THEN '승인' WHEN a.approval_state = 9 THEN '반려' ELSE '알 수 없음' END approvalState, CASE WHEN a.start_time = a.update_start_time AND a.end_time = a.update_end_time THEN 'x' ELSE '사유 확인' END updateStatus, a.update_reason updateReason, DATE_FORMAT(a.final_time, '%Y-%m-%d %H:%i') finalTime FROM attendance a INNER JOIN emp e ON a.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo} AND a.emp_no != #{empNo} AND a.approval_state != 0
AND e.emp_name LIKE '%${search}%'
ORDER BY date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT a.emp_no empNo, e.emp_name empName, a.date date, DATE_FORMAT(a.start_time, '%H:%i') startTime, DATE_FORMAT(a.end_time, '%H:%i') endTime, DATE_FORMAT(a.update_start_time, '%H:%i') updateStartTime, DATE_FORMAT(a.update_end_time, '%H:%i') updateEndTime, CASE WHEN a.approval_state = 1 THEN '미승인' WHEN a.approval_state = 2 THEN '승인' WHEN a.approval_state = 9 THEN '반려' ELSE '알 수 없음' END approvalState, CASE WHEN a.start_time = a.update_start_time AND a.end_time = a.update_end_time THEN 'x' ELSE '사유 확인' END updateStatus, a.update_reason updateReason, DATE_FORMAT(a.final_time, '%Y-%m-%d %H:%i') finalTime FROM attendance a INNER JOIN emp e ON a.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo} AND a.emp_no != #{empNo} AND a.approval_state = #{approvalState}
AND (e.emp_name LIKE '%${search}%')
ORDER BY date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT 
	COUNT(*)
FROM 
	attendance a			 
INNER JOIN 
	emp e ON a.emp_no = e.emp_no
WHERE 
	e.dpt_no = #{dptNo} 
	AND a.emp_no != #{empNo}
	AND a.approval_state = #{approvalState}

mapper.update:
UPDATE 
	attendance 
SET 
	approval_state = 9,
	approval_reason = #{approvalReason}
WHERE
	emp_no = #{empNo}
	AND date = #{date}
