mapper.select:
SELECT e.emp_name empName, e.emp_no empNo, e.emp_email empEmail, e.ext_no extNo, e.start_date startDate, e.signup_YN signupYN, e.rank_name rankName, o.dpt_name officeName, d.dpt_name deptName, t.dpt_name teamName FROM emp e LEFT JOIN `group` t ON t.dpt_no = e.dpt_no LEFT JOIN `group` d ON t.superior_dpt_no = d.dpt_no LEFT JOIN `group` o ON d.superior_dpt_no = o.dpt_no WHERE 1=1 AND emp_no != #{empNo}
AND o.dpt_name = #{empSearchDTO.officeName}
AND d.dpt_name = #{empSearchDTO.deptName}
AND t.dpt_name = #{empSearchDTO.teamName}
AND e.rank_name = #{empSearchDTO.rankName}
AND e.signup_YN = #{empSearchDTO.signupYN}
AND e.emp_no LIKE '%${empSearchDTO.empNo}%'
ORDER BY emp_name LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM emp e LEFT JOIN `group` t ON t.dpt_no = e.dpt_no LEFT JOIN `group` d ON t.superior_dpt_no = d.dpt_no LEFT JOIN `group` o ON d.superior_dpt_no = o.dpt_no WHERE 1=1
AND o.dpt_name = #{officeName}
AND d.dpt_name = #{deptName}
AND t.dpt_name = #{teamName}
AND e.rank_name = #{rankName}
AND e.signup_YN = #{signupYN}
AND e.emp_no LIKE '%${empNo}%'

mapper.update:
UPDATE emp SET
dpt_no = #{dptNo},
end_date = #{endDate}, emp_state = #{empState}, rank_name = #{rankName} WHERE emp_no = #{empNo}
