mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO msg( sender_emp_no, title, content, create_datetime, msg_state ) VALUES ( #{senderEmpNo}, #{title}, #{content}, NOW(), 1
)

mapper.select:
SELECT COUNT(*) FROM ( SELECT m.msg_no FROM msg m INNER JOIN msg_recipient mr ON m.msg_no = mr.msg_no INNER JOIN emp e ON e.emp_no = mr.recipient_emp_no WHERE m.sender_emp_no = #{empNo} AND m.msg_state = 1 GROUP BY m.msg_no
HAVING SUM(CASE WHEN mr.read_YN = 'Y' THEN 1 ELSE 0 END) = COUNT(mr.msg_no)
HAVING SUM(CASE WHEN mr.read_YN = 'Y' THEN 1 ELSE 0 END) != COUNT(mr.msg_no)
) AS s;
