mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY startDatetime) scheduleOrder, schedule_no scheduleNo, emp_no empNo, title, content, type, start_datetime startDatetime, end_datetime endDatetime FROM schedule WHERE emp_no = #{empNo}
AND ( title LIKE '%${search}%' OR content LIKE '%${search}%' OR DATE_FORMAT(start_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(start_datetime, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(end_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(end_datetime, '%m-%d') LIKE '%${search}%' )
AND type = '개인' ORDER BY scheduleOrder DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT '휴가' AS category, '휴가내역' AS title, DATE_FORMAT(doh.start_date, '%Y-%m-%d %H:%i') AS startDate, DATE_FORMAT(doh.end_date, '%Y-%m-%d %H:%i') AS endDate, e.emp_name AS empName, doh.day_off_no AS uniqueNo FROM day_off_history doh JOIN emp e ON doh.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
AND ( '휴가내역' LIKE '%${search}%' OR DATE_FORMAT(doh.start_date, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR DATE_FORMAT(doh.end_date, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
UNION ALL
SELECT '출장' AS category, '출장내역' AS title, DATE_FORMAT(bth.start_date, '%Y-%m-%d %H:%i') AS startDate, DATE_FORMAT(bth.end_date, '%Y-%m-%d %H:%i') AS endDate, e.emp_name AS empName, bth.business_trip_no AS uniqueNo FROM business_trip_history bth JOIN emp e ON bth.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
AND ( '출장내역' LIKE '%${search}%' OR DATE_FORMAT(bth.start_date, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR DATE_FORMAT(bth.end_date, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
UNION ALL
SELECT sc.type AS category, sc.title AS title, DATE_FORMAT(sc.start_datetime, '%Y-%m-%d %H:%i') AS startDate, DATE_FORMAT(sc.end_datetime, '%Y-%m-%d %H:%i') AS endDate, e.emp_name AS empName, sc.schedule_no AS uniqueNo FROM schedule sc JOIN emp e ON sc.emp_no = e.emp_no WHERE sc.type = '팀' AND e.dpt_no = #{dptNo}
AND ( '팀' LIKE '%${search}%' OR sc.title LIKE '%${search}%' OR DATE_FORMAT(sc.start_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR DATE_FORMAT(sc.end_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
ORDER BY startDate DESC LIMIT #{beginRow}, #{rowPerPage};

SELECT '개인' category, sc.title title, DATE_FORMAT(sc.start_datetime, '%Y-%m-%d') startDate, DATE_FORMAT(sc.end_datetime, '%Y-%m-%d') endDate, e.emp_name empName FROM schedule sc JOIN emp e ON sc.emp_no = e.emp_no WHERE sc.emp_no = #{empNo} AND sc.type = '개인'
UNION ALL
SELECT '휴가' category, '휴가' title, DATE_FORMAT(doh.start_date, '%Y-%m-%d') startDate, DATE_FORMAT(doh.end_date, '%Y-%m-%d') endDate, e.emp_name empName FROM day_off_history doh JOIN emp e ON doh.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
UNION ALL
SELECT '출장' category, '출장' title, DATE_FORMAT(bth.start_date, '%Y-%m-%d') startDate, DATE_FORMAT(bth.end_date, '%Y-%m-%d') endDate, e.emp_name empName FROM business_trip_history bth JOIN emp e ON bth.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
UNION ALL
SELECT '팀' category, sc.title title, DATE_FORMAT(sc.start_datetime, '%Y-%m-%d') startDate, DATE_FORMAT(sc.end_datetime, '%Y-%m-%d') endDate, e.emp_name empName FROM schedule sc JOIN emp e ON sc.emp_no = e.emp_no WHERE sc.type = '팀' AND e.dpt_no = #{dptNo}
ORDER BY startDate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT '개인' AS category, r.rsv_no AS rsvNo, ri.room_name AS roomName, r.meeting_title AS meetingTitle, ri.room_place AS roomPlace, r.`type` AS type, DATE_FORMAT(r.start_datetime, '%Y-%m-%d %H:%i') AS startDatetime, DATE_FORMAT(r.end_datetime, '%Y-%m-%d %H:%i') AS endDatetime, e.emp_name AS empName FROM room_rsv r INNER JOIN emp e ON r.emp_no = e.emp_no INNER JOIN room_info ri ON ri.room_no = r.room_no WHERE r.rsv_state = 1 AND r.`type` = 'personal' AND e.emp_no = #{empNo}
AND ( '개인' LIKE '%${search}%' OR r.meeting_title LIKE '%${search}%' OR ri.room_name LIKE '%${search}%' OR ri.room_place LIKE '%${search}%' OR DATE_FORMAT(r.start_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR DATE_FORMAT(r.end_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
UNION ALL
SELECT '팀' AS category, r.rsv_no AS rsvNo, ri.room_name AS roomName, r.meeting_title AS meetingTitle, ri.room_place AS roomPlace, r.`type` AS type, DATE_FORMAT(r.start_datetime, '%Y-%m-%d %H:%i') AS startDatetime, DATE_FORMAT(r.end_datetime, '%Y-%m-%d %H:%i') AS endDatetime, e.emp_name AS empName FROM room_rsv r INNER JOIN emp e ON r.emp_no = e.emp_no INNER JOIN room_info ri ON ri.room_no = r.room_no WHERE r.rsv_state = 1 AND r.`type` = 'team' AND e.dpt_no = #{dptNo}
AND ( '팀' LIKE '%${search}%' OR r.meeting_title LIKE '%${search}%' OR ri.room_name LIKE '%${search}%' OR ri.room_place LIKE '%${search}%' OR DATE_FORMAT(r.start_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR DATE_FORMAT(r.end_datetime, '%Y-%m-%d %H:%i') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
ORDER BY startDatetime DESC LIMIT #{beginRow}, #{rowPerPage};

mapper.update:
UPDATE 
	schedule
SET 
	title = #{scheduleDTO.title},
	content = #{scheduleDTO.content},
	start_datetime = #{scheduleDTO.startDatetime},
	end_datetime = #{scheduleDTO.endDatetime}
WHERE
	emp_no = #{scheduleDTO.empNo} 
AND schedule_no = #{scheduleNo}
