mapper.select:
SELECT schedule_no scheduleNo, emp_no empNo, title, content, type, start_datetime startDatetime, end_datetime endDatetime FROM schedule WHERE emp_no = #{empNo}
AND ( title LIKE '%${search}%' OR content LIKE '%${search}%' OR DATE_FORMAT(start_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(start_datetime, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(end_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(end_datetime, '%m-%d') LIKE '%${search}%' )
AND type = '개인' ORDER BY start_datetime DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT '휴가' category, '휴가내역' title, doh.start_date startDate, doh.end_date endDate, e.emp_name empName, doh.day_off_no uniqueNo FROM day_off_history doh JOIN emp e ON doh.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
AND ( '휴가내역' LIKE '%${search}%' OR DATE_FORMAT(doh.start_date, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(doh.start_date, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(doh.end_date, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(doh.end_date, '%m-%d') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
UNION ALL
SELECT '출장' category, '출장내역' title, bth.start_date startDate, bth.end_date endDate, e.emp_name empName, bth.business_trip_no uniqueNo FROM business_trip_history bth JOIN emp e ON bth.emp_no = e.emp_no WHERE e.dpt_no = #{dptNo}
AND ( '출장내역' LIKE '%${search}%' OR DATE_FORMAT(bth.start_date, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(bth.start_date, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(bth.end_date, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(bth.end_date, '%m-%d') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
UNION ALL
SELECT sc.type category, sc.title title, sc.start_datetime startDate, sc.end_datetime endDate, e.emp_name empName, sc.schedule_no uniqueNo FROM schedule sc JOIN emp e ON sc.emp_no = e.emp_no WHERE sc.type = '팀' AND e.dpt_no = #{dptNo}
AND ( '팀' LIKE '%${search}%' OR sc.title LIKE '%${search}%' OR DATE_FORMAT(sc.start_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(sc.start_datetime, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(sc.end_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(sc.end_datetime, '%m-%d') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
ORDER BY startDate DESC LIMIT #{beginRow}, #{rowPerPage};

SELECT r.rsv_no rsvNo, r.room_no roomNo, '팀회의' title, r.start_datetime startDatetime, r.end_datetime endDatetime, e.emp_name empName FROM room_rsv r INNER JOIN emp e ON r.emp_no = e.emp_no WHERE dpt_no = #{dptNo} AND r.rsv_state = 1
AND ( '팀회의' LIKE '%${search}%' OR DATE_FORMAT(r.start_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(r.start_datetime, '%m-%d') LIKE '%${search}%' OR DATE_FORMAT(r.end_datetime, '%Y-%m-%d %H:%i:%s') LIKE '%${search}%' OR DATE_FORMAT(r.end_datetime, '%m-%d') LIKE '%${search}%' OR e.emp_name LIKE '%${search}%' )
ORDER BY start_datetime DESC LIMIT #{beginRow}, #{rowPerPage};
